SCRIPT_DIR="$(cd "$(dirname "$0")" && pwd)" && set -a && source "$SCRIPT_DIR/.env" && set +a && cd "$SCRIPT_DIR/server" && watchexec --restart --verbose --wrap-process=session --stop-signal SIGTERM --exts gleam,css --watch src/ --watch ../client/src/ --watch priv/static/input.css -- "fuser -k 3000/tcp; timeout 5s sh -c 'while lsof -i :3000; do :; done'; tailwindcss -i priv/static/input.css -o priv/static/output.css; gleam run"
